import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import { Button } from "react-bootstrap";
import "./style.css";
// import { Route,NavLink,BrowserRouter as Router,Switch} from "react-router-dom";
class Me extends React.Component {
  onLogoutClick = (e) => {
    e.preventDefault();
    this.props.logoutUser();
  };
  render() {
    const { user } = this.props.auth;
    return (
    <div>
        <div class="TopBarME TopG">
          <h3 class="Toptitle TitleW">Me</h3>
        </div>

      <div class="PerCon">

      {!user.name ? <h1>user avatar true</h1> :  <img src="/uploads/avatar.png" alt="..."  class="PerAvater" />}

            <span>
              <b><h4 class="userName">{user.name.split(" ")[0]}</h4></b> 
              </span>

              <Button
              style={{
                font: "10px",
                color: "aliceblue",
                borderColor: "#10caa6",
                background: "#10caa6",
                width: "100px",
                height: "30px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem",
              }}
              onClick={this.onLogoutClick}
              className="btn btn-large waves-effect waves-light hoverable accent-3"
            >
              Logout
            </Button>
            {/* <Button
              style={{
                font: "10px",
                color: "white",
                borderColor: "#10caa6",
                background: "#10caa6",
                width: "100px",
                height: "30px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem",
              }}
            >
                  <NavLink to="/dashboard/upload">
                  Upload
                  </NavLink>
                  </Button> */}

        </div>
      </div>
    );
  }
}
Me.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logoutUser })(Me);
