import React from "react";
import spinner from "../dashboard/spiner.gif"
import { Link} from "react-router-dom";

const Articles = ({posts})=>{
 const [article, setArticle] = useState([]);

 //DELETE Article BY ID
 const deleteArticle = id => {
    axios.delete(`/api/articles/${id}`).then(res => alert(res.data));
    setArticle(article.filter(elem => elem._id !==id));

 }

return(

<div>
    {!posts.length ? <img style={{width: "30px"}} src={spinner} alt="loading..." />:

    posts.map((post,key )=> (

        <div className="container" key={key}>
        <Link to={{pathname: `/dashboard/${post._id}`}}>
        <h3 >{post.title}</h3> 
        </Link>
        <div >{post.article}</div>
        <span> {post.authorname}</span> 
        <Link to={`/edit-post/${post._id}`}>
        <button style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"left"
              }} className="btn btn-small waves-effect waves-light  green accent-3">Edit Article
              </button></Link>
        <button onClick={()=> deleteArticle(post._id)} style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"right"
              }} className="btn btn-small waves-effect waves-light  red accent-3">Delete Article</button>


        </div>//container


            ))}
</div>//post end;

)


}

export default Articles;
