import React from "react";
import spinner from "../dashboard/spiner.gif"
import { Link} from "react-router-dom";

const Articles = ({posts})=>{

return(

<div>
    {!posts.length ? <img style={{width: "30px"}} src={spinner} alt="loading..." />:

    posts.map((post,key )=> (

        <div className="container" key={key}>
        <Link to={{pathname: `/dashboard/article/newarticle/${article._id}`}}>
        <h3 >{post.title}</h3> 
        </Link>
        <div >{post.article}</div>
        <span> {post.authorname}</span> 
        <button style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"left"
              }} className="btn btn-small waves-effect waves-light  green accent-3">Edit Article</button>
        <button style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"right"
              }} className="btn btn-small waves-effect waves-light  red accent-3">Delete Article</button>


        </div>//container


            ))}
</div>//post end;

)


}

export default Articles;
