import React from "react";


const Articles = ({posts})=>{

return(

<div>
    {!posts.length ? <img src={spinner} alt="loading..." />:

    posts.map((post,key )=> (

        <div className="container">
        <h3 >{post.title}</h3> 
        <div >{post.article}</div>
        <span> {post.authorname}</span> 
        <button style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"left"
              }} className="btn btn-small waves-effect waves-light  green accent-3">Edit Article</button>
        <button style={{
                width: "80px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "2rem",
                float:"right"
              }} className="btn btn-small waves-effect waves-light  red accent-3">Delete Article</button>


        </div>//container


            ))}
</div>//post end;

)


}

export default Articles;
