import React, {useState,useEffect} from "react";

import axios from 'axios';


const EditPost = (props)=>{
    const [title, setTitle] = useState("");
    const [article, setArticle] = useState("");
    const [authorname, setAuthorName] = useState("");
    const [message, setMessage] = useState("");

    const changeOnClick = e => {
        e.preventDefault();

        const articles = {
            title,
            article,
            authorname
        };

        setTitle("");
        setArticle("");
        setAuthorName("");
    axios
    .put("/api/articles/update/:id", articles)
    .then(res => console.log(res.data))
    .catch(err =>{
        console.log(err);
    })
    }

    useEffect(() => {
        axios
            .get(`/api/articles/${props.match.params.id}`)
            .then(res => {
                setTitle(res.data.title),
                setArticle(res.data.article),
                setAuthorName(res.data.authorname)
            })
            .catch(error => console.log(error));
    }, [])

return(



<div className="row">
    <form className="col s12" onSubmit={changeOnClick} encType="multipart/form-data">

      <div className="row">
        <div className="input-field col 12">
          <input onChange={e => setAuthorName(e.target.value)} value={authorname} id="authorname" type="text" className="validate" />
          <label htmlFor="authorname">Author Name</label>
        </div>
      </div>

      <div className="row">
        <div className="input-field col 12">
          <input   onChange={e => setTitle(e.target.value)} value={title}id="title" type="text" className="validate" />
          <label htmlFor="title">Title</label>
        </div>
      </div>

      <div className="row">
        <div className="input-field col s12">
          <textarea  onChange={e => setArticle(e.target.value)} value={article} id="article" className="materialize-textarea"></textarea>
          <label htmlFor="article">Textarea</label>
        </div>
      </div>
      <button className="btn waves-effect waves-light" type="submit" >Submit
        </button>

    </form>
  </div>


)


}

export default EditPost;
