import React, {useState, useEffect} from "react";
import axios from "axios";
import spiner from "../dashboard/spiner-1.gif";



const Newarticle = (props)=>{
    const [title, setTitle] = useState("");
    const [article, setArticle] = useState("");
    const [authorname, setAuthorName] = useState("");
    const [articleImage, setArticleImage] = useState("");

    useEffect(() => {
        axios
            .get(`/api/articles/${props.match.params.id}`)
            .then(res => (
                setTitle(res.data.title),
                setArticle(res.data.article),
                setAuthorName(res.data.authorname),
                setArticleImage(res.data.articleImage)
            ))
            .catch(error => console.log(error));
    }, [props])
return(

<div>
    {!title || !article || !authorname ? (
        <img style={{width: "30px"}} src={spiner} alt="loading..." />
    ) : (
        <>

        <div class="TopBar TopG">
            <h2 class="Toptitle TitleW">MISFits Article</h2>
        </div>

        <div class="ArticleCon">
            <img src={`/uploads/${articleImage}`} alt="..." class="homeBanner"/>


            <h2 class="DetailTextTile ">{title}</h2>
            <p>{article}</p>
            <span>{authorname}</span>
        </div>
        </>
    )}

</div>

)


}

export default Newarticle;
